import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

const root = ReactDOM.createRoot(document.getElementById('root'));

function StudentComp(props){

  let list = StudentsListComp();
  return(
    <>
    {
      list.map(s => <p style={s.grade <= 56 ? {color: "red"} : s.grade >= 57 && s.grade <= 75 ? {color: 'yellow'}: s.grade > 75 && s.grade <= 85 ? {color: 'orange'} :{color: 'green'}}> {`${s.id} - ${s.name} - ${s.grade}`}</p>)
    }

    </>
  );
}
function StudentsListComp(){
  let studentList = [
  {id:1,name:"or shani",grade:100},
  {id:1,name:"or lo shani",grade:10},
  {id:1,name:"shmulik",grade:57},
  {id:1,name:"Mishu",grade:76},
  {id:1,name:"ze ah",grade:76},
  {id:1,name:"ze hamor",grade:100},

  ];
  return studentList;
}


root.render(
  <React.StrictMode>
    <StudentComp />
  </React.StrictMode>
);
